sys_script_include.script:
var TestDemoUtils = Class.create();
TestDemoUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	insertIncData: function(requestData){


		var stgRec = new GlideRecord('x_94128_snow_incid_test_inc_integration');
		stgRec.initialize();
		stgRec.u_short_description = requestData['short_description'];
		stgRec.u_description = requestData['description'];
		stgRec.u_impact = requestData['impact'];
		stgRec.u_urgency = requestData['urgency'];
		stgRec.u_correlation_id = requestData['correlation_id'];
		stgRec.u_number = requestData['number'];

		stgRec.insert();

		var grInc = new GlideRecord('incident');
		grInc.get('correlation_id', stgRec.u_correlation_id);


		return {'number': grInc.number};

	},

    type: 'TestDemoUtils'
});
